import React, { useContext, useEffect, useState } from 'react';
import '../../styles/IndividualRestaurant.css';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom'; // Added useNavigate here
import { GeneralContext } from '../../context/GeneralContext';  // Make sure this import is correct

const IndividualRestaurant = () => {
  const { restaurantId } = useParams();  // Assume you're fetching data based on restaurant ID
  const navigate = useNavigate();  // useNavigate is now correctly defined

  const [restaurant, setRestaurant] = useState(null);
  const [availableCategories, setAvailableCategories] = useState([]);
  const [sortFilter, setSortFilter] = useState('');  // State for sorting/filtering

  // Fetch restaurant details
  useEffect(() => {
    const fetchRestaurant = async () => {
      try {
        const response = await fetch(`/api/restaurants/${restaurantId}`);
        const data = await response.json();
        setRestaurant(data);
      } catch (error) {
        console.error('Error fetching restaurant data:', error);
      }
    };

    fetchRestaurant();
  }, [restaurantId]);

  // Fetch categories (you can update the URL or data source as per your backend)
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('/api/categories');
        const data = await response.json();
        setAvailableCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchCategories();
  }, []);

  // Handle changes in sorting/filtering
  const handleSortFilterChange = (event) => {
    setSortFilter(event.target.value);
    console.log('Sort/filter changed:', event.target.value);
    // Here you can implement sorting/filtering logic
  };

  // Handle category checkbox change
  const handleCategoryCheckBox = (category) => {
    console.log(`Category selected: ${category}`);
    // Implement category filter logic here
  };

  // Handle restaurant type filter change
  const handleTypeCheckBox = (type) => {
    console.log(`Type selected: ${type}`);
    // Implement type filter logic here
  };

  if (!restaurant) {
    return <div>Loading restaurant data...</div>;
  }

  return (
    <div>
      <h1>{restaurant.name}</h1>
      <p>{restaurant.description}</p>

      {/* Sort Filter Dropdown */}
      <div>
        <label>Sort by:</label>
        <select onChange={handleSortFilterChange}>
          <option value="price">Price</option>
          <option value="rating">Rating</option>
          <option value="name">Name</option>
        </select>
      </div>

      {/* Available Categories
      <div>
        <h2>Available Categories</h2>
        {availableCategories.length > 0 ? (
          <AvailableCategories categories={availableCategories} onCheck={handleCategoryCheckBox} />
        ) : (
          <p>No categories available</p>
        )}
      </div> */}

      {/* Restaurant Types (checkboxes) */}
      <div>
        <h3>Filter by Type</h3>
        <input
          type="checkbox"
          id="fast-food"
          onChange={() => handleTypeCheckBox('fast-food')}
        />
        <label htmlFor="fast-food">Fast Food</label>

        <input
          type="checkbox"
          id="vegan"
          onChange={() => handleTypeCheckBox('vegan')}
        />
        <label htmlFor="vegan">Vegan</label>

        <input
          type="checkbox"
          id="vegetarian"
          onChange={() => handleTypeCheckBox('vegetarian')}
        />
        <label htmlFor="vegetarian">Vegetarian</label>
      </div>

      {/* Display restaurant menu or products (for example) */}
      <div>
        <h3>Menu</h3>
        {/* Add your code to display restaurant menu */}
      </div>
    </div>
  );
};

export default IndividualRestaurant;
