import React, { useContext, useEffect, useState } from 'react'
import '../../styles/IndividualRestaurant.css'
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { GeneralContext } from '../../context/GeneralContext';

const IndividualRestaurant = () => {
  const { fetchCartCount } = useContext(GeneralContext);
  const userId = localStorage.getItem('userId');
  const { id } = useParams();
  
  const [restaurant, setRestaurant] = useState(null);
  const [AvailableCategories, setAvailableCategories] = useState([]);
  const [items, setItems] = useState([]);
  const [visibleItems, setVisibleItems] = useState([]);
  
  const [sortFilter, setSortFilter] = useState('popularity');
  const [categoryFilter, setCategoryFilter] = useState([]);
  const [typeFilter, setTypeFilter] = useState([]);
  
  const [cartItem, setCartItem] = useState('');
  const [quantity, setQuantity] = useState(0);

  useEffect(() => {
    fetchCategories();
    fetchItems();
    fetchRestaurants();
  }, []); // Add fetchRestaurants if you want it as a dependency

  const fetchRestaurants = async () => {
    try {
      const response = await axios.get(`http://localhost:6001/fetch-restaurant/${id}`);
      setRestaurant(response.data);
    } catch (err) {
      console.log(err);
    }
  }

  const fetchCategories = async () => {
    try {
      const response = await axios.get('http://localhost:6001/fetch-categories');
      setAvailableCategories(response.data);
    } catch (err) {
      console.log(err);
    }
  }

  const fetchItems = async () => {
    try {
      const response = await axios.get('http://localhost:6001/fetch-items');
      setItems(response.data);
      setVisibleItems(response.data);
    } catch (err) {
      console.log(err);
    }
  }

  const handleCategoryCheckBox = (e) => {
    const value = e.target.value;
    setCategoryFilter(prevState => 
      e.target.checked ? [...prevState, value] : prevState.filter(item => item !== value)
    );
  }

  const handleTypeCheckBox = (e) => {
    const value = e.target.value;
    setTypeFilter(prevState => 
      e.target.checked ? [...prevState, value] : prevState.filter(item => item !== value)
    );
  }

  const handleSortFilterChange = (e) => {
    const value = e.target.value;
    setSortFilter(value);
    
    let sortedItems = [...visibleItems];
    switch (value) {
      case 'low-price':
        sortedItems.sort((a, b) => a.price - b.price);
        break;
      case 'high-price':
        sortedItems.sort((a, b) => b.price - a.price);
        break;
      case 'discount':
        sortedItems.sort((a, b) => b.discount - a.discount);
        break;
      case 'rating':
        sortedItems.sort((a, b) => b.rating - a.rating);
        break;
      default:
        break;
    }
    setVisibleItems(sortedItems);
  }

  useEffect(() => {
    if (categoryFilter.length > 0 || typeFilter.length > 0) {
      const filteredItems = items.filter(product => 
        (categoryFilter.length === 0 || categoryFilter.includes(product.menuCategory)) &&
        (typeFilter.length === 0 || typeFilter.includes(product.category))
      );
      setVisibleItems(filteredItems);
    } else {
      setVisibleItems(items);
    }
  }, [categoryFilter, typeFilter, items]);

  const handleAddToCart = async (foodItemId, foodItemName, restaurantId, foodItemImg, price, discount) => {
    try {
      await axios.post('http://localhost:6001/add-to-cart', { userId, foodItemId, foodItemName, restaurantId, foodItemImg, price, discount, quantity });
      alert("Product added to cart!");
      setCartItem('');
      setQuantity(0);
      fetchCartCount();
    } catch (err) {
      alert("Operation failed!");
    }
  }

  return (
    <div className="IndividualRestaurant-page">
      {restaurant ? (
        <>
          <h2>{restaurant.title}</h2>
          <p>{restaurant.address}</p>

          <div className="IndividualRestaurant-body">
            <div className="restaurants-filter">
              <h4>Filters</h4>
              <div className="restaurant-filters-body">

                {/* Add filters here */}
                
              </div>
            </div>

            <div className="restaurants-body">
              <h3>All Items</h3>
              <div className="restaurants">
                {visibleItems.filter(item => item.restaurantId === restaurant._id).map((item) => (
                  <div className="restaurant-item" key={item._id}>
                    <div className="restaurant">
                      <img src={item.itemImg} alt={item.title} />
                      <div className="restaurant-data">
                        <h6>{item.title}</h6>
                        <p>{item.description.slice(0, 25)}...</p>
                        <h6>&#8377; {parseInt(item.price - (item.price * item.discount) / 100)} <s>{item.price}</s></h6>
                        {cartItem === item._id ? (
                          <>
                            <input type="number" value={quantity} onChange={(e) => setQuantity(e.target.value)} />
                            <button className="btn-add-to-cart" onClick={() => handleAddToCart(item._id, item.title, restaurant._id, item.itemImg, item.price, item.discount)}>Update Cart</button>
                          </>
                        ) : (
                          <button className="btn-add-to-cart" onClick={() => setCartItem(item._id)}>Add to Cart</button>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default IndividualRestaurant;
