import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom'; // If you plan to use it
import { RestaurantContext } from '../context/RestaurantContext'; // Assuming you have a context

const IndividualRestaurant = () => {
  const navigate = useNavigate();
  const { restaurantData, fetchRestaurants } = useContext(RestaurantContext);
  const [sortFilter, setSortFilter] = useState('popularity');
  const [visibleItems, setVisibleItems] = useState(restaurantData);
  
  // Function to handle the sorting
  const handleSortFilterChange = (e) => {
    const value = e.target.value;
    setSortFilter(value);

    let sortedItems = [...visibleItems];
    switch (value) {
      case 'low-price':
        sortedItems.sort((a, b) => a.price - b.price);
        break;
      case 'high-price':
        sortedItems.sort((a, b) => b.price - a.price);
        break;
      case 'discount':
        sortedItems.sort((a, b) => b.discount - a.discount);
        break;
      case 'rating':
        sortedItems.sort((a, b) => b.rating - a.rating);
        break;
      default:
        break;
    }
    setVisibleItems(sortedItems);
  };

  // Fetch the restaurants when the component mounts
  useEffect(() => {
    fetchRestaurants();
  }, [fetchRestaurants]);

  // JSX structure
  return (
    <div>
      <h1>Restaurant: {restaurantData.name}</h1>
      <p>{restaurantData.description}</p>

      {/* Sorting filter section */}
      <div>
        <label>
          Sort by:
          <select value={sortFilter} onChange={handleSortFilterChange}>
            <option value="popularity">Popularity</option>
            <option value="low-price">Low Price</option>
            <option value="high-price">High Price</option>
            <option value="discount">Discount</option>
            <option value="rating">Rating</option>
          </select>
        </label>
      </div>

      {/* Category filtering */}
      <AvailableCategories />

      {/* Menu items rendering */}
      <div>
        <h2>Menu</h2>
        <ul>
          {visibleItems.map((item) => (
            <li key={item.id}>
              <h3>{item.name}</h3>
              <p>Price: ${item.price}</p>
              <p>Rating: {item.rating}</p>
              <p>Discount: {item.discount}%</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default IndividualRestaurant;
